import torch, torch.nn as nn

class GatedBlock(nn.Module):
    def __init__(self, block):
        super().__init__()
        self.block = block
        self.w = nn.Parameter(torch.zeros(1))  # initial gate ~0.5
    def forward(self, x, t):
        gate = torch.sigmoid(self.w * (1. - t).view(-1, 1, 1, 1))
        if self.training:
            return x + gate * (self.block(x, t) - x), gate.mean()
        else:  # STE binarisation
            hard_gate = (gate > 0.5).float()
            return x + hard_gate * (self.block(x, t) - x), hard_gate.mean()

